import { Component } from '@angular/core';
import { HomeService } from '../services/home.service';
declare var google: any;

@Component({
    selector: 'home',
    styles: [``],
    template: `
            <h1>My Angular Google Map</h1>
            <div id="map"></div>
    `
})
export class HomeComponent 
{
    data: any;
    constructor(public home: HomeService)
    {
        this.initMapCallback = this.initMapCallback.bind(this);
    }

    ngOnInit()
    {
        this.getData();
        this.loadGoogleMapsScript();
    }
    getData(): void 
    {
        this.home.getData().subscribe((result) => {
            this.data = result;
            console.log(this.data);
        });
    }    



    loadGoogleMapsScript() {
        const script = document.createElement('script');
        script.src = `https://maps.googleapis.com/maps/api/js?key=AIzaSyAQCxWfFd4zjq7a3NTw5fjnNcS_hyi--rw&callback=initMapCallback`;
        //script.src = `https://maps.googleapis.com/maps/api/js?key=AIzaSyAQCxWfFd4zjq7a3NTw5fjnNcS_hyi--rw&callback=${this.initMapCallback}`;
        script.async = true;
        script.defer = true;
        document.body.appendChild(script);
      }

      initMapCallback() {
        // Initialize and display the map
        const map = new google.maps.Map(document.getElementById('map'), {
          center: { lat: -34.397, lng: 150.644 },
          zoom: 8
        });
      }    

    // Example POST operation
    postData(): void 
    {
        const newData = {Name: 'Davor'};
        this.home.postData(newData).subscribe(() => {
                console.log('Data inserted successfully');
                // Optionally, fetch updated data after insertion
                this.getData();
            },
            (error) => {
                console.error('Error inserting data:', error);
            }
        );
    }      
}
